select 

from SUC_DRIVER_SCHOOL


    and DRIVER_NAME = #{driverName,jdbcType=VARCHAR}


    and DRIVER_REMARK = #{driverRemark,jdbcType=VARCHAR}


    and FOUND_TIME = #{foundTime,jdbcType=DATE}


    and ADDRESS = #{address,jdbcType=VARCHAR}


    and YEAR_STUDENT = #{yearStudent,jdbcType=BIGINT}


    and PASSING_RATE = #{passingRate,jdbcType=TINYINT}


    and DELIVER_DETAIL = #{deliverDetail,jdbcType=VARCHAR}


    and DRIVER_IMG = #{driverImg,jdbcType=VARCHAR}


    and SCHOOL_DETAIL = #{schoolDetail,jdbcType=VARCHAR}


    and TEACHER_COUNT = #{teacherCount,jdbcType=INTEGER}


    and CAR_COUNT = #{carCount,jdbcType=INTEGER}


    and TRAINING_COUNT = #{trainingCount,jdbcType=INTEGER}


    and CREAT_TIME = #{creatTime,jdbcType=DATE}


    and ALL_RATE = #{allRate,jdbcType=DECIMAL}


    and SIGNUP_PRICE = #{signupPrice,jdbcType=INTEGER}


    and ALL_PRICE = #{allPrice,jdbcType=INTEGER}


select count(*) 
from SUC_DRIVER_SCHOOL
where ID = #{id,jdbcType=BIGINT}


