select 

from SUC_GOODS


    and CLASS_ID = #{classId,jdbcType=BIGINT}


    and GOOD_TITLE = #{goodTitle,jdbcType=VARCHAR}


    and GOOD_DETAIL = #{goodDetail,jdbcType=VARCHAR}


    and STATE = #{state,jdbcType=BIT}


    and GOOD_TYPE = #{goodType,jdbcType=TINYINT}


    and OLD_PRICE = #{oldPrice,jdbcType=INTEGER}


    and NEW_PRICE = #{newPrice,jdbcType=INTEGER}


    and BUY_TIME = #{buyTime,jdbcType=DATE}


    and PRICE_DAY = #{priceDay,jdbcType=INTEGER}


    and IS_NOW_SELL = #{isNowSell,jdbcType=BIT}


    and IS_DISCUSS = #{isDiscuss,jdbcType=BIT}


    and USER_ID = #{userId,jdbcType=BIGINT}


    and AOTU_DOWN_TIME = #{aotuDownTime,jdbcType=DATE}


