select 

from SUC_USER_DRIVER


    and USER_ID = #{userId,jdbcType=BIGINT}


    and STATE = #{state,jdbcType=TINYINT}


    and TAINING_ID = #{tainingId,jdbcType=BIGINT}


    and SIGNUP_TIME = #{signupTime,jdbcType=DATE}


    and CANCEL_TIME = #{cancelTime,jdbcType=DATE}


    and SIGNING_TIME = #{signingTime,jdbcType=DATE}


    and FINISH_TIME = #{finishTime,jdbcType=DATE}


    and CANCEL_REASON = #{cancelReason,jdbcType=VARCHAR}


    and COMMENT = #{comment,jdbcType=VARCHAR}


    and RATE = #{rate,jdbcType=DECIMAL}


    and RECOMMENDER = #{recommender,jdbcType=VARCHAR}


    and USER_NAME = #{userName,jdbcType=VARCHAR}


    and MOBILE_PHONE = #{mobilePhone,jdbcType=BIGINT}


    and CONTRACT_PATH = #{contractPath,jdbcType=VARCHAR}


    and DRIVER_ID = #{driverId,jdbcType=BIGINT}


