    and FIELD_ID = #{fieldId,jdbcType=BIGINT}


    and SIGNUP_TIME = #{signupTime,jdbcType=DATE}


    and CANCEL_TIME = #{cancelTime,jdbcType=DATE}


    and SIGNING_TIMe = #{signingTime,jdbcType=DATE}


    and FINISH_TIME = #{finishTime,jdbcType=DATE}


    and CANCEL_REASON = #{cancelReason,jdbcType=CHAR}


    and COMMENT = #{comment,jdbcType=VARCHAR}


    and RATE = #{rate,jdbcType=DECIMAL}


    and RECOMMENDER = #{recommender,jdbcType=VARCHAR}


